const AppJS = {
  // Register Components
  components: {
    DataExtensionSearch,
    DataExtensionResults,
  },
  // Data function returns $data object
  data() {
    return {
      config: {
        apiBaseURL:
          "https://mct6kk2l3spmvz7h3-s46x07swk0.pub.sfmc-content.com/1udssm1fr4q",
      },
      dataExtensionReturn: [],
    };
  },
  methods: {
    submitSearch: async function (searchTerm) {
      try {
        if (!searchTerm) {
          return;
        }

        const requestDataExtensionSearch = await axios.post(
          `${this.config.apiBaseURL}?route=getDataExtensions`,
          {
            searchTerm,
          }
        );
          console.log(requestDataExtensionSearch)
        if (
          requestDataExtensionSearch &&
          requestDataExtensionSearch.data.status &&
          requestDataExtensionSearch.data.status === "ok" &&
          requestDataExtensionSearch.data.response &&
          requestDataExtensionSearch.data.response.length
        ) {
            this.dataExtensionReturn = requestDataExtensionSearch.data.response
        }
      } catch (err) {
        console.log(err);
      }
    },
  },
};

// Initiate/configuration of main Vue Application
const app = Vue.createApp(AppJS).mount("#app");

const DataExtensionResults = {
components: {
    ResultsListItem
},
props: {
dataExtensionReturn: Array
},
template: `
<section>
    <div class="w-full max-w-4xl mx-auto mt-20">
        <div class="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
            <h2 class="mb-2 text-lg font-semibold text-gray-900 dark:text-dark">Search Results:</h2>
            <div v-for="(result, index) in dataExtensionReturn" :key="result.CustomerKey">
                <results-list-item :result="result"></results-list-item>
                <!-- <ul class="pt-6 max-w-md space-y-1 text-gray-500 list-none list-inside dark:text-gray-400">
                    <li class="text-md font-bold">
                        {{result.Name}}
                    </li>
                    <li>
                        Customer Key: {{result.CustomerKey}}
                    </li>
                    <li>
                        Created Date: {{ result.CreatedDate}}
                    </li>
                    <li>
                        Category ID: {{ result.CategoryID}}
                    </li>
                </ul> -->
            </div>
        </div>
    </div>
</section>
`,
};

const DataExtensionSearch = {
    data() {
      return {
        formData: {
          dataExtensionName: ''
        },
      };
    },
    methods: {
      submitSearch: function () {
        if(this.formData.dataExtensionName){
          this.$emit('submitSearchRequest', this.formData.dataExtensionName)
        } else {
          return
        }
      }
    },
    template: `
      <section class="formWrapper">
        <div class="w-full max-w-4xl mx-auto mt-20">
            <form class="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                <div class="mb-4">
                    <label class="block text-gray-700 text-sm font-bold mb-2" for="dataExtensionName">
                        Data Extension Name
                    </label>
                    <input
                        class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        id="dataExtensionName" type="text" v-model="formData.dataExtensionName">
                </div>
                <div class="flex items-center justify-between">
                    <button
                        class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        type="button"
                        @click="submitSearch"
                        >
                        Submit
                    </button>
                </div>
            </form>
        </div>
    </section>
        `,
  };

const ResultsListItem = {
    props: {
        result: {
            Name: {
                type: String
            },
            CustomerKey: {
                type: String
            },
            CategoryID: {
                type: Number
            }
        }
        },
    template: `
<section>
    <ul class="pt-6 max-w-md space-y-1 text-gray-500 list-none list-inside dark:text-gray-400">
        <li class="text-md font-bold text-clifford">
            {{result.Name}}
        </li>
        <li>
            Customer Key: {{result.CustomerKey}}
        </li>
        <li>
            Created Date: {{ result.CreatedDate}}
        </li>
        <li>
            Category ID: {{ result.CategoryID}}
        </li>
    </ul>
</section>
`,
};
